func TestBearer(t *testing.T) {
	at := awstoken.New("dev", "us-east-1")

	token := "Bearer eyJraWQiOiJUczY3cE95Rk5tU2I3S3BkS1NIdXRjRDZ4XC81ZEFBYys2Wm1SR1l6YmVTcz0iLCJhbGciOiJSUzI1NiJ9..j8psr_dX-Mekyq2ASepoqTiZFa8Cx5WwoMLxWjTo80KsfPZQvMS7952VdK_PdoViBplhmjHo_GSiBzaYBIZs4Q3vKPUdNJLRacA-CNyWDBSFn020EJZtS3b1uUt9lcjBZywrajh3Z1BcPIq-eMyHges-hA1GxGASFwmcT3DdGxl8imjurnGrbh14KWzSqjsUigUCkOElljRGg3vIPpLDD9CSfooRhK_fVlz2xL1DcTH4BdPJr4-_EeWt7ujP0P7E6Onh63q7AOxKriirpf1tiHtpnGnJDg-G8jOEHeO0A1v6atmwOTUu_fTYxcI6QjYQMNtMP7ryIc6WpYlNdIFneg"
	tokenParts := strings.Split(token, "Bearer ")
	if len(tokenParts) != 2 {
		t.Fatalf("failed to split string")
	}
	t.Logf("%s\n", tokenParts[1])
	_, err := at.UnsafeExtractAccess(context.Background(), tokenParts[1])
	if err != nil {
		t.Fatalf("error extracting token: %v\n", err)
	}
}

